import React, { useEffect, useState } from 'react';
import Select, { MultiValue, OptionsOrGroups } from 'react-select';
import { Flex } from '../../defaultStyles';

import { Category, Course, Courses, } from '../../types';

export const TrainingCourses: React.FC = () => {
  const perPage = 3;
  const [totalPages, setTotalPages] = useState(1);
  const [page, setPage] = useState(1);
  const [coursesList, setcoursesList] = useState<Course[]>([]);
  const [loading, setLoading] = useState(false);
  const [apiData, setApiData] = useState<Courses>();
  const [categories, setCategories] = useState<Category[]>([]);
  const [selectedCategories, setSelectedCategories] = useState<MultiValue<{ value: string; label: string; }>>([]);

  let url = `http://frontend-trial-api.qa.parallax.dev/api/courses`;
  if (page > 1) {
    url = apiData?.next_page_url || url
  }
  const categoriesUrl = 'https://frontend-trial-api.qa.parallax.dev/api/categories';
  useEffect(() => {
    const getCategories = () => {
      setLoading(true);
      fetch(categoriesUrl)
        .then(res => res.json())
        .then(res => {
          console.log(res)
          setCategories(res.courses.data)
          setLoading(false);
        });
    }
    getCategories();

    const getcoursesList = () => {
      setLoading(true);
      fetch(url)
        .then(res => res.json())
        .then(res => {
          setTotalPages(res.last_page);
          setcoursesList([...coursesList, ...res.courses.data]);
          setApiData(res.courses)
          setLoading(false);
        });
    }
    getcoursesList();
  }, [page]);

  let categoriesOptions: { value: string; label: string }[] = [];
  // Make unique
  categories.map(x => categoriesOptions?.push({ value: x.slug, label: x.name }));

  const filterCourses = () => {
    // POST request using fetch inside useEffect React hook
    console.log(selectedCategories.map(x => x.value));

    const requestOptions = {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        "categories": selectedCategories.map(x => x.value),
        "locations": ["leeds"]
      }
      )
    };
    fetch('https://frontend-trial-api.qa.parallax.dev/api/courses', requestOptions)
      .then(response => response.json())
      .then(res => {
        console.log(res);
        setTotalPages(res.last_page);
        setcoursesList(res.courses.data);
        setApiData(res.courses)
        setLoading(false);
      });
  };

  useEffect(() => {
    filterCourses()
  }, [selectedCategories])
  return (
    <div className="App">
      <Select
        placeholder="Category"
        onChange={(newValue: MultiValue<{ value: string; label: string; }>) => {
          setSelectedCategories(newValue)
        }}
        options={categoriesOptions}
        isMulti={true}
      />

      {coursesList?.map((x: Course, i) => {
        // Tidy up
        let name = x?.name?.replace('<p>', '').replace('</p>', '');
        return(
        <>
      <table>
  <thead>
    <tr>
      <th>Course</th>
      <th>ID</th>
      <th>Price</th>
      <th>Location</th>
      <th>Dates</th>
      <th>Link</th>
    </tr>
  </thead>
  <tbody>
    {coursesList.map(item => {
      return (
        <tr key={item.course_id}>
          <td>{ item.id }</td>
          <td>{ item.price }</td>
          <td>{ item.location }</td>
          <td>{`${item.starts_at} - ${item.ends_at}`}</td>
        </tr>
      );
    })}
  </tbody>
</table>
      {apiData?.current_page !== apiData?.last_page && <button className="btn-load-more" onClick={() => setPage(page + 1)}>{loading ? 'Loading...' : 'Load More '}</button>}
  </>
        )} )}
        </div>
  )};
