import { useEffect, useState} from 'react';
import axios from 'axios';
import { Course } from '../components/trainingCourses';
type Props = {
  url: string;
}
type Link = {
  active: boolean;
label: string | null;
url:  string |null;
}
type Courses_Server = {
  courses: {
    current_page: string | null;
data: Course[],
first_page_url: string | null;
from: string | null;
last_page: string | null;
last_page_url: string | null;
links: Link[];
next_page_url: string | null;
path: string | null;
per_page: string | null;
prev_page_url: string | null;
to: number;
total: number;
};
errors: [];
success: boolean
};

const useCourses = (url: string) => {
  const [data, setData] = useState<Courses_Server>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const { data: response } = await axios.get(url);
        setData(response);
      } catch (error) {
        console.error(error)
      }
      setLoading(false);
    };

    fetchData();
  }, []);

  return {
    data,
    loading,
  };
};

export default useCourses;
