import { Category } from "../types";

type Props = {
    setLoading: (loading: boolean) => void;
    setCategories: (categories: Category[]) => void;
    url: string
};

export const getCategories = ({setLoading, setCategories, url}:Props) => {    
    setLoading(true);
    fetch(url)
      .then(res => res.json())
      .then(res => {
        console.log(res)
        setCategories(res.courses.data)
        setLoading(false);
      });
  }
