import { MultiValue } from "react-select";
import { Course, Courses } from "../types";

type Props = {
    setLoading: (loading: boolean) => void;
    selectedCategories: MultiValue<{ value: string; label: string; }>;
    selectedLocations: MultiValue<{ value: string; label: string; }>;
    setCoursesList: (courseList: Course[]) => void;
    setApiData: (apiData: Courses) => void;
};

export const filterCourses = ({ setLoading, selectedCategories, selectedLocations, setCoursesList, setApiData }: Props) => {

    setLoading(true);
    const requestOptions = {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
            "categories": selectedCategories.map(x => x.value),
            "locations": selectedLocations.map(x => x.value),
        }
        )
    };
    fetch('https://frontend-trial-api.qa.parallax.dev/api/courses', requestOptions)
        .then(response => response.json())
        .then(res => {
            console.log(res);
            setCoursesList(res.courses.data);
            setApiData(res.courses)
            setLoading(false);
        });
};
