import React, { useMemo } from 'react';


import useFetchData from "../../customHooks/useFetchData";
import { H1 } from "../../defaultStyles";
export type Course = {
  category: string;
  course_id: string;
  created_at: Date | null
  ends_at: Date | null
  id: number;
  link: "/courses/intro-to-parallax"
  location: string;
  name: string;
  price: number;
  slug: string;
  starts_at: Date | null;
  updated_at: Date | null
}

export const TrainingCourses: React.FC = () => {
  const url = 'https://frontend-trial-api.qa.parallax.dev/api/courses'

  //?page=2
  const {
    data,
    loading,
  } = useFetchData(url);

  console.log(loading)
  console.log(data);
  const columns = React.useMemo(
    () => [
      {
        Header: 'Course',
        accessor: 'course',
      },
      {
        Header: 'ID',
        accessor: 'id',
      },
      {
        Header: 'Price',
        accessor: 'price',
      },
      {
        Header: 'Location',
        accessor: 'location',
      },
      {
        Header: 'Dates',
        accessor: 'starts_at',
      },
      {
        Header: 'Link',
        accessor: 'link',
      },
    ],
    []
  )
  const tableData = useMemo(() => data, []);
  return (
    loading ?
      (<div>loading...</div>)
      :
      (
        <>
          <H1>Training Courses</H1>
          <Table columns={columns} data={data} />
        </>
      )

  )
}
