import { Course, Courses } from "../types";

type Props = {
    setLoading: (loading: boolean) => void;
    setCoursesList: (courses: Course[]) => void;
    setApiData: (courses: Courses) => void;
    coursesList: Course[]; 
    url: string
};

export const getCoursesList = ({setLoading, setCoursesList, setApiData, coursesList, url}:Props) => {
    setLoading(true);
    fetch(url)
      .then(res => res.json())
      .then(res => {
        setCoursesList([...coursesList, ...res.courses.data]);
        setApiData(res.courses)
        setLoading(false);
      });
  }
