import React, { useEffect, useState } from 'react';

import useFetchData from "../../customHooks/useFetchData";
import { FlexColumn, H1 } from "../../defaultStyles";

export type Course = {
  category: string;
  course_id: string;
  created_at: Date | null
  ends_at: Date | null
  id: number;
  link: "/courses/intro-to-parallax"
  location: string;
  name: string;
  price: number;
  slug: string;
  starts_at: Date | null;
  updated_at: Date | null
}

export const TrainingCourses: React.FC = () => {
  const perPage = 3;
  const [totalPages, setTotalPages] = useState(1);
  const [page, setPage] = useState(1);

  const [data, setData] = useState<Course[]>([]);
  const [loading, setLoading] = useState(false);

  const url = 'https://frontend-trial-api.qa.parallax.dev/api/courses'
  "http://frontend-trial-api.qa.parallax.dev/api/courses?page=2"

  // const {
  //   data,
  //   loading,
  // } = useFetchData(url);

  useEffect(() => {
    const getdata = () => {
      setLoading(true);
      fetch(`http://frontend-trial-api.qa.parallax.dev/api/courses?${page}`)
        .then(res => res.json())
        .then(res => {
          setTotalPages(res.courses.total);
          setData([...data, ...res.courses.data]);
          setLoading(false);
        });
    }
    getdata();
  }, [page]);

  // console.log(loading)
  // console.log(data);

  return (
    loading ?
      (<div>loading...</div>)
      :
      (
        <FlexColumn>
          <H1>Training Courses</H1>
          {
            data?.courses?.data.map(x => {
              return (
                <p>{x.name}</p>
              )
            })
          }
          // Can't use data?.courses for some reason
          // Come back to this
          {
            data &&
            data.courses &&
            data.courses.current_page &&
            data.courses.last_page &&
            data.courses.current_page < data.courses.last_page &&
            (<button onClick={() => setPage(page + 1)}>Load more</button>)}
        </FlexColumn>
      )

  )
}
