import React, { useEffect, useState } from 'react';

import { Courses } from '../../types';

export const TrainingCourses: React.FC = () => {
  const perPage = 3;
  const [totalPages, setTotalPages] = useState(1);
  const [page, setPage] = useState(1);
  const [coursesList, setcoursesList] = useState<any>([]);
  const [loading, setLoading] = useState(false);
  const [apiData, setApiData] = useState<Courses>();

 let url = `http://frontend-trial-api.qa.parallax.dev/api/courses`;
 if(page > 1) {
   url = apiData?.next_page_url || url
 }
 console.log(apiData)
  useEffect(() => {
    const getcoursesList = () => {
      setLoading(true);
      fetch(url)
        .then(res => res.json())
        .then(res => {
          setTotalPages(res.total_pages);
          setcoursesList([...coursesList, ...res.courses.data]);
          setApiData(res.courses)
          setLoading(false);
        });
    }
    getcoursesList();
  }, [page]);

  return (
    <div className="App">
      {coursesList.map((x: { name: string; }, i: React.Key | null | undefined) => {
        return <div key={i} className="box">
          {x.name}
        </div>
      })}
      <div className="clearfix"></div>
      {totalPages !== page && <button className="btn-load-more" onClick={() => setPage(page + 1)}>{loading ? 'Loading...' : 'Load More'}</button>}
    </div>
  );
}
