import React, { useEffect, useState } from 'react';
import Select, { OptionsOrGroups } from 'react-select';

import { Category, Course, Courses } from '../../types';

export const TrainingCourses: React.FC = () => {
  function unique(array: any[]) {
    return array.reduce(
      (unique, item) => (unique.includes(item) ? unique : [...unique, item]),
      [],
    );
  }
  const perPage = 3;
  const [totalPages, setTotalPages] = useState(1);
  const [page, setPage] = useState(1);
  const [coursesList, setcoursesList] = useState<Course[]>([]);
  const [loading, setLoading] = useState(false);
  const [apiData, setApiData] = useState<Courses>();
  const [categories, setCategories] = useState<Category[]>([]);
  const [selectedCategories, setSelectedCategories] = useState<any[]>([])
  console.log(coursesList)
  let url = `http://frontend-trial-api.qa.parallax.dev/api/courses`;
  if (page > 1) {
    url = apiData?.next_page_url || url
  }
  const categoriesUrl = 'https://frontend-trial-api.qa.parallax.dev/api/categories';
  console.log(selectedCategories)
  useEffect(() => {
    const getCategories = () => {
      setLoading(true);
      fetch(categoriesUrl)
        .then(res => res.json())
        .then(res => {
          console.log(res)
          setCategories(res.courses.data)
          setLoading(false);
        });
    }
    getCategories();

    const getcoursesList = () => {
      setLoading(true);
      fetch(url)
        .then(res => res.json())
        .then(res => {
          setTotalPages(res.last_page);
          setcoursesList([...coursesList, ...res.courses.data]);
          setApiData(res.courses)
          setLoading(false);
        });
    }
    getcoursesList();
  }, [page]);
  let categoriesOptions: { value: string; label: string }[] = [];
  // Make unique
  categories.map(x => categoriesOptions?.push({ value: x.name, label: x.name }));

  const filterCourses = () => {
    // POST request using fetch inside useEffect React hook
    const requestOptions = {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        "categories": selectedCategories,
        "locations": ["leeds"]
    }
    )
    };
    fetch('https://frontend-trial-api.qa.parallax.dev/api/courses', requestOptions)
      .then(response => response.json())
      .then(data =>   setcoursesList([...coursesList, ...data.courses.data]));
  };


  return (
    <div className="App">
      <Select
        placeholder="Category"
        onChange={(value) => {
          setSelectedCategories([value])
          filterCourses()
        }}
        options={categoriesOptions}
        isMulti={true}
      />

      {coursesList?.map((x: Course, i) => {
        // Tidy up
        let name = x?.name?.replace('<p>', '').replace('</p>', '');
        return <div key={i} className="box">
          {`${name} ${x.category} ${x.location}`}
        </div>
      })}
      {apiData?.current_page !== apiData?.last_page && <button className="btn-load-more" onClick={() => setPage(page + 1)}>{loading ? 'Loading...' : 'Load More '}</button>}
    </div>
  );
}
