import React, { useEffect, useState } from 'react';
import Select, { MultiValue } from 'react-select';
import { getCategories } from '../../fetches/getCategories';
import { getcoursesList } from '../../fetches/getCourseList';
import { getLocations } from '../../fetches/getLocation';

import { Category, Course, Courses, Location } from '../../types';
import { Button } from '../Button';
import { CourseTable } from '../table';

export const TrainingCourses: React.FC = () => {

  const [page, setPage] = useState(1);
  const [coursesList, setcoursesList] = useState<Course[]>([]);
  const [loading, setLoading] = useState(false);
  const [apiData, setApiData] = useState<Courses>();
  const [categories, setCategories] = useState<Category[]>([]);
  const [selectedCategories, setSelectedCategories] = useState<MultiValue<{ value: string; label: string; }>>([]);
  const [locations, setLocations] = useState<Location[]>([]);
  const [selectedLocations, setSelectedLocations] = useState<MultiValue<{ value: string; label: string; }>>([]);

  let url = `http://frontend-trial-api.qa.parallax.dev/api/courses`;
  if (page > 1) {
    url = apiData?.next_page_url || url
  }
  const categoriesUrl = 'https://frontend-trial-api.qa.parallax.dev/api/categories';
  const locationUrl = 'https://frontend-trial-api.qa.parallax.dev/api/locations';
  // const getCategories = () => {
  //   setLoading(true);
  //   fetch(categoriesUrl)
  //     .then(res => res.json())
  //     .then(res => {
  //       console.log(res)
  //       setCategories(res.courses.data)
  //       setLoading(false);
  //     });
  // }
  const getLocation = () => {
    setLoading(true);
    fetch(locationUrl)
      .then(res => res.json())
      .then(res => {
        console.log(res)
        setLocations(res.courses.data)
        setLoading(false);
      });
  }
  // const getcoursesList = () => {
  //   setLoading(true);
  //   fetch(url)
  //     .then(res => res.json())
  //     .then(res => {
  //       setcoursesList([...coursesList, ...res.courses.data]);
  //       setApiData(res.courses)
  //       setLoading(false);
  //     });
  // }
  useEffect(() => {
    getCategories({setLoading, setCategories});
    getLocation({setLoading, setLocations});
    getcoursesList({setLoading, setcoursesList, setApiData, coursesList, url});
  }, [page]);

  let categoriesOptions: { value: string; label: string }[] = [];
  // Make unique
  categories.map(x => categoriesOptions?.push({ value: x.slug, label: x.name }));

  let locationOptions: { value: string; label: string }[] = [];
  // // Make unique
  locations.map(x => locationOptions?.push({ value: x.slug, label: x.name }));

  const filterCourses = () => {
    // POST request using fetch inside useEffect React hook
    console.log(selectedCategories.map(x => x.value));
    setLoading(true);
    const requestOptions = {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        "categories": selectedCategories.map(x => x.value),
        "locations": selectedLocations.map(x => x.value),
      }
      )
    };
    fetch('https://frontend-trial-api.qa.parallax.dev/api/courses', requestOptions)
      .then(response => response.json())
      .then(res => {
        console.log(res);
        setcoursesList(res.courses.data);
        setApiData(res.courses)
        setLoading(false);
      });
  };

  useEffect(() => {
    if (selectedCategories.length > 0) {
      filterCourses();
    } else {
      getcoursesList({setLoading, setcoursesList, setApiData, coursesList, url});
    }

  }, [selectedCategories, selectedLocations]);

  return (
    <div className="App">
      <Select
        placeholder="Category"
        onChange={(newValue: MultiValue<{ value: string; label: string; }>) => {
          setSelectedCategories(newValue)
        }}
        options={categoriesOptions}
        isMulti={true}
        isClearable={true}
      />
      <Select
        placeholder="Location"
        onChange={(newValue: MultiValue<{ value: string; label: string; }>) => {
          setSelectedLocations(newValue)
        }}
        options={locationOptions}
        isMulti={true}
        isClearable={true}
      />

      <CourseTable coursesList={coursesList} />

      {apiData?.current_page !== apiData?.last_page && (
        <Button onClick={() => setPage(page + 1)} title={loading ? "Loading..." : "Load more"} />
      )}
    </div>
  )
};
