import React, { useEffect, useState } from 'react';
import Select from 'react-select';

import { Category, Course, Courses, SelectOption } from '../../types';

export const TrainingCourses: React.FC = () => {
  const perPage = 3;
  const [totalPages, setTotalPages] = useState(1);
  const [page, setPage] = useState(1);
  const [coursesList, setcoursesList] = useState<Course[]>([]);
  const [loading, setLoading] = useState(false);
  const [apiData, setApiData] = useState<Courses>();
  const [categories, setCategories] = useState<Category[]>([]);

  let url = `http://frontend-trial-api.qa.parallax.dev/api/courses`;
  if (page > 1) {
    url = apiData?.next_page_url || url
  }
  const categoriesUrl = 'https://frontend-trial-api.qa.parallax.dev/api/categories';

  useEffect(() => {
    const getCategories = () => {
      setLoading(true);
      fetch(categoriesUrl)
        .then(res => res.json())
        .then(res => {
          console.log(res)
          setCategories(res.courses.data)
          setLoading(false);
        });
    }
    getCategories();

    const getcoursesList = () => {
      setLoading(true);
      fetch(url)
        .then(res => res.json())
        .then(res => {
          setTotalPages(res.last_page);
          setcoursesList([...coursesList, ...res.courses.data]);
          setApiData(res.courses)
          setLoading(false);
        });
    }
    getcoursesList();
  }, [page]);

  const categoryOptions: SelectOption[] = categories.map(category => ({ value: category.name, label: category.name }));


  return (
    <div className="App">
      <Select options={categoryOptions} />

      {coursesList.map((x: Course, i) => {
        // Tidy up
        let name = x?.name?.replace('<p>', '').replace('</p>', '');
        return <div key={i} className="box">
          {`${name} ${x.category} ${x.location}`}
        </div>
      })}
      {apiData?.current_page !== apiData?.last_page && <button className="btn-load-more" onClick={() => setPage(page + 1)}>{loading ? 'Loading...' : 'Load More '}</button>}
    </div>
  );
}
