import React, { useEffect, useState } from 'react';

import { FlexColumn, H1 } from "../../defaultStyles";

 type Server = {
  courses: Course;
errors: any[];
success: boolean;
}
export type Course = {
  category: string;
  course_id: string;
  created_at: Date | null
  ends_at: Date | null
  id: number;
  link: "/courses/intro-to-parallax"
  location: string;
  name: string;
  price: number;
  slug: string;
  starts_at: Date | null;
  updated_at: Date | null
}

export const TrainingCourses: React.FC = () => {
  const perPage = 3;
  const [totalPages, setTotalPages] = useState(1);
  const [page, setPage] = useState(1);
  const [coursesList, setcoursesList] = useState<any>([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const getcoursesList = () => {
      setLoading(true);
      fetch(`https://frontend-trial-api.qa.parallax.dev/api/courses&page=${page}`)
        .then(res => res.json())
        .then(res => {
          setTotalPages(res.total_pages);
          setcoursesList([...coursesList, ...res.data]);
          setLoading(false);
        });
    }
    getcoursesList();
  }, [page]);

  return (
    <div className="App">
      {coursesList.map((x: { avatar: string | undefined; first_name: boolean | React.ReactChild | React.ReactFragment | React.ReactPortal | null | undefined; last_name: boolean | React.ReactChild | React.ReactFragment | React.ReactPortal | null | undefined; email: boolean | React.ReactChild | React.ReactFragment | React.ReactPortal | null | undefined; }, i: React.Key | null | undefined) => {
        return <div key={i} className="box">
          <img src={x.avatar} />
          <div className="name">{x.first_name} {x.last_name}</div>
          <div className="email">{x.email}</div>
        </div>
      })}
      <div className="clearfix"></div>
      {totalPages !== page && <button className="btn-load-more" onClick={() => setPage(page + 1)}>{loading ? 'Loading...' : 'Load More'}</button>}
    </div>
  );
}
