import styled from "styled-components";

import { Course } from "../../types";

const TableWrapper = styled.div`
    background: #FFFFFF;
    border-radius: 8px;
    padding: 40px;
    table {
        border-collapse: collapse;
        width: 100%;
    }   
    th {
        border-bottom: 1pt solid #292C3E;
        font-family: 'Inter';
        font-style: normal;
        font-weight: 700;
        font-size: 14px;
        line-height: 24px;
        color: #292C3E;
        text-align: left;
    }
    td {
        font-family: 'Inter';
        font-style: normal;
        font-weight: 400;
        font-size: 12px;
        line-height: 24px;
        color: #292C3E;
    }
    td.title{
        font-weight: 700;
        font-size: 14px;
    }
`;

export const CourseTable: React.FC<{ coursesList: Course[] }> = ({ coursesList }) => {
    return (
        <TableWrapper>
            <table>
                <thead>
                    <tr>
                        <th>Course</th>
                        <th>Category</th>
                        <th>ID</th>
                        <th>Price</th>
                        <th>Location</th>
                        <th>Dates</th>
                        <th>Link</th>
                    </tr>
                </thead>
                <tbody>
                    {coursesList.map(item => {
                        return (
                            <tr key={item.id}>
                                <td className="title">{item.course_id}</td>
                                <td>{item.category}</td>
                                <td>{item.id}</td>
                                <td>{item.price}</td>
                                <td>{item.location}</td>
                                <td>{`${item.starts_at} - ${item.ends_at}`}</td>
                                <td>Register</td>
                            </tr>
                        );
                    })}
                </tbody>
            </table>
            {apiData?.current_page !== apiData?.last_page && (
                <FlexCenter>
                    <Button onClick={() => setPage(page + 1)} title={loading ? "Loading..." : "Load more"} />
                </FlexCenter>
            )}
        </TableWrapper>
    )
}
